        end: "-=500", // end after scrolling 500px beyond the start
        scrub: 1, // smooth scrubbing, takes 1 second to "catch up" to the scrollbar

      },
      // Remove the extra markup when it's done
      onComplete: () => {
        parentSplit.revert();
        childSplit.revert();
      }
    });



}

